import React, {useState} from 'react';
import validation from './validation';
import { Container } from '../styles/Container.styled';
import { Appwrapper } from '../styles/Appwrapper.styled';
import { Title } from '../styles/title.styled';
import { inputwrapper } from '../styles/input.styled';

const Form = () => {

    const [inputs, setInput] = useState ({
        businessname: "",
        businesswebsite: "",
        ticketingsystem: "",
        email: "",
        password: "",
        termofservice: "",
    });

    const changeHandler = (e) => {
        setInput({
            ...inputs,
            [e.target.name]: e.target.value,
        });
    };

    const [errors, setErrors] = useState({});

    const handleFormSubmit = (e) => {
        e.preventDefault();
        setErrors(validation(inputs));
    };



    return (
        <Container>
            <Appwrapper>
                <div>
                    <h2 className="title">Tell us about yourself</h2>
                </div>
                <form inputwrapper>
                    <div className="name">
                        <label className="label">What is the name of your business</label>
                        <input className="input" type="text" name="businessname" value={inputs.businessname} onChange={changeHandler}/>
                        {errors.businessname && <p className="errors">{errors.businessname}</p>}
                    </div>
                    <div className="name">
                        <label className="label">Business website</label>
                        <input className="input" type="text" name="businesswebsite" value={inputs.businesswebsite} onChange={changeHandler}/>
                        {errors.businesswebsite && <p className="errors">{errors.businesswebsite}</p>}
                    </div>
                    <div className="name">
                        <label className="label">Select your ticketing system</label>
                        <input className="input" type="text" name="ticketingsystem" value={inputs.ticketingsystem} onChange={changeHandler}/>
                        {errors.ticketingsystem && <p className="errors">{errors.ticketingsystem}</p>}
                    </div>
                    <div className="name">
                        <label className="label">Your email</label>
                        <input className="input" type="email" name="email" value={inputs.email} onChange={changeHandler}/>
                        {errors.email && <p className="errors">{errors.email}</p>}
                    </div>
                    <div className="name">
                        <label className="label">Password</label>
                        <input className="input" type="password" name="password" value={inputs.password} onChange={changeHandler}/>
                        {errors.password && <p className="errors">{errors.password}</p>}
                    </div>
                    <div>
                    <input type="checkbox" id="termofservice" name="termofservice" value={inputs.termofservice} onChange={changeHandler}/>
                    <label for="termofservice"> I accept to the Terms of Service</label>
                    
                    </div>
                    <div>
                        <button className="submit" onClick={handleFormSubmit}>Sign Up</button>
                    </div>
                </form>
            </Appwrapper>
        </Container>
    )
}

export default Form;
